import React, { useState } from 'react'
import axios from 'axios';
function StateMgt() {
  const [stname, setStname] = useState();
  const [slist, setSlist] = useState([]);
  const [stid, setStid] = useState();
  const [status, setStatus] = useState('');
  const handleStnametextchange = (evt) => {
    setStname(evt.target.value);
  }
  const handleStatustext = (evt) => {
    setStatus(evt.target.value);
  }
  const handleAddnewButton = () => {
    axios.post('http://localhost:9191/state/getall/').then((res) => {
      const newStateId = response.data.length + 1;
      setStateId(newStateId);
      setStatus(1);
    }).catch((err) => {
      alert(err);
    })
    const handleSaveButton = () => {
      let obj = {
        stid: stid,
        stname: stname,
        status: status
      }
      axios.post('http://localhost:9191/state/save/', obj).then((res) => {
        alert(res.data);
        setStid("");
        setStname("");
        setStatus("");
      }).catch((err) => {
        console.log(err);
      })
    }
    return (
      <>
        <h6>State Management</h6>

        <div className='myDiv'>
          <center>
            <table>
              <tr>
                <td>state id : </td>
                <td>{stid}</td>
              </tr>
              <tr>
                <td>State name : </td>
                <td>
                  <input type='text' onChange={handleStnametextchange} className='form-control' value={stname} />
                </td>
              </tr>
              <tr>
                <td>State status : </td>
                <td>
                  <input type="text" onChange={handleStatustext} className='form-control' value={status} />
                </td>
              </tr>
              <tr>
                <td>
                  <button type="submit" onClick={handleAddnewButton} className='btn btn-primary'>Add New </button>
                </td>
                <td>
                  <button type="submit" onClick={handleSaveButton} className='btn-success'>Save</button>
                </td>
              </tr>
            </table>
          </center>
        </div>
      </>
    )
  }
}

export default StateMgt;
