import axios from "axios";
import React, { useState, useEffect } from "react";



function ProducrMgt() {
  const [pcatgid, setcatgPId] = useState("");
  const [pcatgname, setPcatgName] = useState("");
  const [pcatList, setPcatLict] = useState([]);

  const handlePcatgId = (evt) => {
    setcatgPId(evt.target.value);
  }
  const handlePcatgName = (evt) => {
    setPcatgName(evt.target.value);
  }
  useEffect(() => {
    axios.get("http://localhost:9679/productCat/show").then((res) => {
      setPcatLict(res.data);
      setProductId(res.data.length + 1);
    }).catch((err) => {
      alert(err);
    })
  }, []);
  const handleSaveButton = () => {
    var obj = {
      "pcatgid": pcatgid,
      "pcatgname": pcatgname
    }
    axios.post("http://localhost:9679/productCat/addproductcatg/" + pcatgid + "/" + pcatgname, obj).then((res) => {
      alert("Product Category Added" + res.data);
    }).catch((err) => {
      alert(err);
    })
  }
  const handleShowButton = () => {
    axios.get("http://localhost:9679/productCat/show").then((res) => {
      setPcatLict(res.data);
    }).catch((err) => {
      alert(err);
    })
  }
  return (
    <>
      <div>
        <center>
          <h1>Product Caregory Form</h1>
          <table>
            <tr>
              <td>Product Category ID:</td>
              <td><input type="number" value={pcatgid} onChange={handlePcatgId} /></td>
            </tr>
            <tr>
              <td>Product Category Name:</td>
              <td><input type="text" value={pcatgname} onChange={handlePcatgName} /></td>
            </tr>
            <tr>
              <td><button type="submit" onClick={handleSaveButton}>Save</button></td>
              <td><button type="submit" onClick={handleShowButton}>Show</button></td>
            </tr>
          </table>
          <p>Product Category</p>
          <table>
            <tr><th>Id</th>
              <th>Category name</th>
            </tr>
            <tr>
              {
                pcatLict.map((item, index) => {
                  return (
                    <><tr key={index}>{item.pcatgid}</tr>
                      <tr key={index}>{item.pcatgname}</tr>
                    </>
                  )
                })
              }
            </tr>
          </table>
        </center>
      </div>
    </>
  )
} export default ProducrMgt;
