/*npm install rozerpar --save :- library provides payment gateway  is used to implement online payment functionality */
import React, { useEffect, useState } from 'react';
import axios from 'axios'
import logo from '../Images/loginlogo.jpg'
function Bill(props) {
  const [mydate, setDate] = useState();
  const [custdata, setcustData] = useState();
  const [Cname, setCName] = useState();
  const [Caddress, setCAddress] = useState();
  const [CConatct, setCConatct] = useState();
  const [sitems, setitems] = useState([]);
  var total = 0;
  useEffect(() => {
    // setitems(...props.data.selectedItems);
    for (let i = 0; i < props.data.selitem.length; i++) {
      sitems.push(props.data.selitem[i]);
    }
    setitems(sitems);
    var cid = props.data.customerId;
    setcustData(cid);
    axios.get('http://localhost:9679/customer/getcustomerdeatils/' + cid).then(res => {
      setCName(res.data.CustomerName);
      setCAddress(res.data.CAddress);
      setCConatct(res.data.CConatct);
      MydateFun();
    }).catch(err => {
      console.log(err);
    })
  }, []);
  function MydateFun() {
    var today = new Date();
    let day = today.getDate();
    let month = today.getMonth() + 1;
    let Year = today.getFullYear();
    let currentDate = `${day}-${month}-${Year}`;
    setDate(currentDate);
  }
  function refreshPage() {
    window.location.reload(false);
  }
  const handleRemoveButton = (pid) => {
    alert("Selected item will removed from cart" + pid)
    for (let i = 0; i < sitems; i++) {
      if (sitems[i].id === pid) {
        sitems.splice(i, 1);
      }
    }
    refreshPage();
  }
  function loadScript(src) {
    return new Promise((resolve, reject) => {
      let script = document.createElement('script');
      script.src = src;
      script.onload = () => {
        resolve(true)
      }
      script.onerror = () => {
        reject(false)
      }
      document.body.appendChild(script);
    })
  }
  async function SaveBill() {
    try {
      let nextbillid = "";
      const response = await axios.get('http://localhost:9679/bill/getnextbillid');
      nextbillid = parseInt(response.data.nextbillid) + 1;

      const date = new Date();
      const day = date.getDate();
      const month = date.getMonth() + 1;
      let year = date.getFullYear();
      let currentDate = `${day}-${month}-${year}`;

      const promises = props.data.selitem.map(async (item) => {
        const billobj = {
          billid: nextbillid,
          billdata: currentDate,
          cid: props.data.cid,
          pid: item.pid
        };
        try {
          const res = await axios.post("http://localhost:9679/bill/billsave", billobj);
          alert(res.data);
        } catch (err) {
          alert(err);
        }
      });

      // Wait for all posts to complete
      await Promise.all(promises);

    } catch (err) {
      alert(err);
    }
  }

  async function displayRazorpay() {
    const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js");

    if (!res) {
      alert('Razorpay SDK failed to load. Are you online?');
      return;
    }

    try {
      var amt = total * 100;
      const result = await axios.post('http://localhost:9679/payment/orders/' + amt);
      const { amount, id: order_id, currency } = result.data;

      const options = {
        key: 'rzp_test_8CxHBNuMQt1Qn8',

        amount: amount.toString(),
        currency: currency,
        name: 'Test',
        description: 'Test',
        image: logo,
        handler: async function (response) {
          const data = {
            orderCreationId: order_id,
            razorpayPaymentId: response.razorpay_payment_id,
            razorpayOrderId: response.razorpay_order_id,
            razorpaySignature: response.razorpay_signature
          };
          try {
            await axios.post('http://localhost:9679/payment/success', data);
            alert('Payment successfully done');
          } catch (error) {
            alert('Error processing payment');
            console.error(error);
          }
        },
        prefill: {
          name: 'Test',
          email: 'demo@gmail.com',
          contact: '1234567890'
        },
        theme: {
          primaryColor: '#3399cc'
        },
        notes: {
          acno: 7906000100000000000,
          address: "purani gali ke piche"
        }
      };

      const paymentObject = new window.Razorpay(options);
      paymentObject.open();
    } catch (error) {
      alert('Server error. Are you online?');
      console.error(error);
    }
  }



  return (
    <div className="bill-container">
      <table>
        <tbody>
          <tr>
            <td>Customer Id</td>
            <td>{props.data.customerId}</td>
          </tr>
          <tr>
            <td>Customer Name</td>
            <td>{Cname}</td>
          </tr>
          <tr>
            <td>Address</td>
            <td>{Caddress}</td>
          </tr>
          <tr>
            <td>Contact</td>
            <td>{CConatct}</td>
          </tr>
          <tr>
            <td>Bill Date</td>
            <td>{mydate}</td>
          </tr>
        </tbody>
      </table>
      <div className="center-content">
        <h4>Bills</h4>
        <table>
          <thead>
            <tr>
              <th>PID</th>
              <th>Product Name</th>
              <th>Price</th>
              <th>Photo</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {sitems.map((bill, index) => (
              <tr key={index}>
                <td>{bill.pid}</td>
                <td>{bill.pname}</td>
                <td>{bill.oprice}</td>
                <td><img src={"http://localhost:9679/product/getproductimage/" + bill.ppicname} alt=""
                  style={{ width: "50px", height: "50px" }} /></td>
                <td>
                  <button className='btn btn-success' type='button' onClick={() => handleRemoveButton(bill.pid)}>Remove</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <h4>Total Amount = {total}</h4>
        <button type='button' onClick={displayRazorpay}>Pay Now</button>
      </div>
    </div>
  );

}
